import React, { useEffect } from "react";
import { CLIENT_ID, REDIRECT_URI, AUTH_ENDPOINT } from "./config";

const Login = ({ setToken }) => {
    const RESPONSE_TYPE = "token";
    const SCOPES = [
        "user-read-currently-playing",
        "user-read-playback-state",
        "playlist-read-private",
        "user-top-read",
    ];
    const SCOPES_URL_PARAM = SCOPES.join("%20");

    const handleLogin = () => {
        window.location.href = `${AUTH_ENDPOINT}?client_id=${CLIENT_ID}&scope=${SCOPES_URL_PARAM}&redirect_uri=${REDIRECT_URI}&response_type=${RESPONSE_TYPE}`;
    };


    useEffect(() => {
        const hash = window.location.hash;
        const storedToken = window.localStorage.getItem("token");

        if (!storedToken && hash) {
            const newToken = hash.substring(1).split("&").find(elem => elem.startsWith("access_token")).split("=")[1];

            window.location.hash = "";
            window.localStorage.setItem("token", newToken);
            setToken(newToken);
        }
    }, [setToken]);

    return (
        <div>
            {!localStorage.getItem("token") && <a href="#" onClick={handleLogin}>Login to Spotify</a>}
        </div>
    );
};

export default Login;
