import styles from './SearchBar.module.css'

const SearchBar = ({ token, updateTracklist }) => {
    // Remove searchTerm state and input field

    // Define variables for authentication
    const CLIENT_ID = "a8f0bd6bcf8b44fa9736810f3b9020f2";
    const REDIRECT_URI = "http://localhost:3000/";
    const AUTH_ENDPOINT = "https://accounts.spotify.com/authorize"
    const RESPONSE_TYPE = "token"
    const SCOPE = ["user-top-read"];

    // Submit handler
    const submitHandler = async (e) => {
        e.preventDefault();

        try {
            const response = await fetch(
                `https://api.spotify.com/v1/me/top/tracks?limit=10`, // You can adjust the limit as needed
                {
                    method: "GET",
                    headers: {
                        Authorization: `Bearer ${token}`,
                    },
                }
            );

            if (!response.ok) {
                localStorage.removeItem('token');
                window.location.reload();
                throw new Error("Network response was not ok");
            }

            const data = await response.json();
            const tracks = data.items; // Adjust to the correct structure of the response

            updateTracklist(tracks);
        } catch (error) {
            console.error("Error (SearchBar):", error);
        }
    };

    return (
        <div className={styles.searchBar}>
            {!token ? (
                <button>
                    <a href={`${AUTH_ENDPOINT}?client_id=${CLIENT_ID}&redirect_uri=${REDIRECT_URI}&response_type=${RESPONSE_TYPE}&scope=${SCOPE}`}>Login to Spotify</a>
                </button>)
                :
                (
                    <form onSubmit={submitHandler}>
                        <button type="submit">Show Top Songs</button>
                    </form>
                )}
        </div>
    )
}

export default SearchBar;
