import React, { useEffect, useState } from "react";
import axios from 'axios';

function TopArtists() {
    const [token, setToken] = useState("");
    const [artists, setArtists] = useState([]);

    useEffect(() => {
        const storedToken = window.localStorage.getItem("token");
        setToken(storedToken);
    }, []);

    const fetchTopArtists = async () => {
        try {
            const { data } = await axios.get('https://api.spotify.com/v1/me/top/artists?limit=10', {
                headers: {
                    Authorization: `Bearer ${token}`
                },
            });

            setArtists(data.items);
        } catch (error) {
            console.error("Error fetching top artists:", error);
        }
    };

    return (
        <div>
            <h2>Top Artists</h2>
            <button className="my-button" onClick={fetchTopArtists}>Fetch Top Artists</button>

            {token && artists.length > 0 ? (
                <div>
                    {artists.map(artist => (
                        <div key={artist.id}>
                            {artist.name}
                        </div>
                    ))}
                </div>
            ) : (
                <h2>Please fetch top artists</h2>
            )}
        </div>
    );
}

export default TopArtists;
