// AuthContext.js
import React, { createContext, useContext, useEffect, useState } from "react";

const AuthContext = createContext();

export const useAuth = () => {
  return useContext(AuthContext);
};

export const AuthProvider = ({ children }) => {
  const [authState, setAuthState] = useState({
    accessToken: localStorage.getItem("accessToken") || null,
    tokenType: localStorage.getItem("tokenType") || null,
    expiresIn: localStorage.getItem("expiresIn") || null,
  });

  useEffect(() => {
    // You can perform additional setup or checks here if needed
  }, []);

  const setAccessToken = (accessToken) => {
    setAuthState((prevAuthState) => ({
      ...prevAuthState,
      accessToken,
    }));
  };

  const logout = () => {
    // Clear authentication-related data
    localStorage.clear();
    setAuthState({
      accessToken: null,
      tokenType: null,
      expiresIn: null,
    });
  };

  const isAuthenticated = () => {
    // Customize this based on your authentication logic
    return Boolean(authState.accessToken);
  };

  const contextValue = {
    ...authState,
    setAccessToken,
    logout,
    isAuthenticated,
  };

  return (
    <AuthContext.Provider value={contextValue}>{children}</AuthContext.Provider>
  );
};
