import styles from "./TopSongs.module.css";
import {
  CLIENT_ID,
  REDIRECT_URI,
  AUTH_ENDPOINT,
  RESPONSE_TYPE,
  SCOPE,
} from "../../config";

const TopSongs = ({ token, updateTracklist }) => {
    const submitHandler = async (e) => {
        e.preventDefault();

        try {
          console.log("Fetching top tracks...");

          const response = await window.fetch(  // Use window.fetch explicitly
            `https://api.spotify.com/v1/me/top/tracks`,
            {
              method: "GET",
              headers: {
                Authorization: `Bearer ${token}`,
              },
            }
          );

          console.log("Response:", response);

          if (!response.ok) {
            localStorage.removeItem("token");
            console.error("Network response was not ok");
            // Handle the error gracefully here (e.g., show an error message)
            return;
          }

          const data = await response.json();
          const tracks = data.items;

          updateTracklist(tracks);
        } catch (error) {
          console.error("Error (TopSongs):", error);
        }
      };


  return (
    <div className={styles.TopSongs}>
      {!token ? (
        <button>
          <a
            href={`${AUTH_ENDPOINT}?client_id=${CLIENT_ID}&response_type=${RESPONSE_TYPE}&redirect_uri=${REDIRECT_URI}&scope=${SCOPE}`}
          >
            Login to Spotify
          </a>
        </button>
      ) : (
        <button onClick={submitHandler}>Show my Top 20 Songs</button>
      )}
    </div>
  );
};

export default TopSongs;
