        window.location = `${SPOTIFY_AUTHORIZE_ENDPOINT}?client_id=${CLIENT_ID}&redirect_uri=${REDIRECT_URL}&scope=${SCOPES_URL_PARAM}&response_type=token&show_dialog=true`

    }
    return (
        <div className="Home">
        <h1>TopTunes</h1>
        <p>Create a playlist with your most listened songs</p>    
        <h1>How it works</h1>
        <p>T</p>
        <button className="login-button" onClick={handleLogin}>Login to Spotify</button>
        </div>
    );
};

export default Home;
