import React from "react";
import { Link } from "react-router-dom";
import { AUTH_ENDPOINT, CLIENT_ID, REDIRECT_URI, RESPONSE_TYPE, SCOPES } from "./config";

const SCOPES_URL_PARAM = SCOPES.join("%20");

const Login = () => {
    return (
        <div>
            <h1>Spotify React</h1>
            <Link to={`${AUTH_ENDPOINT}?client_id=${CLIENT_ID}&scope=${SCOPES_URL_PARAM}&redirect_uri=${REDIRECT_URI}&response_type=${RESPONSE_TYPE}`}>
                Login to Spotify
            </Link>
        </div>
    );
};

export default Login;
