import React, { useEffect, useState } from "react";
import "./WebApp.css";
import axios from "axios";

const PLAYLISTS_ENDPOINT = "https://api.spotify.com/v1/me/playlists";

// Function to fetch user data from the Spotify endpoint
const getUserData = async (token) => {
  try {
    const response = await axios.get("https://api.spotify.com/v1/me", {
      headers: {
        Authorization: "Bearer " + token,
      },
    });
    return response.data;
  } catch (error) {
    console.error("Error fetching user data:", error);
    throw error;
  }
};

const WebApp = () => {
  const [token, setToken] = useState("");
  const [data, setData] = useState({});
  const [user, setUser] = useState(null);

  useEffect(() => {
    if (localStorage.getItem("accessToken")) {
      setToken(localStorage.getItem("accessToken"));
    }
  }, []);

  useEffect(() => {
    const fetchData = async () => {
      try {
        if (token) {
          const userData = await getUserData(token);
          setUser(userData);
        }
      } catch (error) {
        // Handle error (optional)
      }
    };

    fetchData();
  }, [token]);

  const handleGetPlaylists = () => {
    axios
      .get(PLAYLISTS_ENDPOINT, {
        headers: {
          Authorization: "Bearer " + token,
        },
      })
      .then((response) => {
        setData(response.data);
      })
      .catch((error) => {
        console.log(error);
      });
  };

  return (
    <>
      <button className="my-button" onClick={handleGetPlaylists}>
        Get Playlists
      </button>
      {data?.items ? data.items.map((item) => <p key={item.id}>{item.name}</p>) : null}

      {user && (
        <div>
          <h2>User Information</h2>
          <p>Name: {user.display_name}</p>
          <p>Email: {user.email}</p>
          {/* Include any other user information you want to display */}
        </div>
      )}
    </>
  );
};

export default WebApp;
