import './App.css';
import TopSongs from './components/topsongs/TopSongs';
import Playlist from './components/playlist/Playlist';
import Tracklist from './components/tracklist/Tracklist';
import { useState, useEffect, useCallback } from 'react';

function App() {
  // define states
  const [tracklist, setTracklist] = useState([]);
  const [playlist, setPlaylist] = useState([]);
  const [playlistName, setPlaylistName] = useState('');
  const [token, setToken] = useState("");

  // on mount: grab token stored in local storage
  useEffect(() => {
    const hash = window.location.hash;
    let token = window.localStorage.getItem("token");

    if (!token && hash) {
      token = hash.substring(1).split("&").find(elem => elem.startsWith("access_token")).split("=")[1];
      window.location.hash = "";
      window.localStorage.setItem("token", token);
    } 
    setToken(token);
  }, []);

  // function to update tracklist, will be called by TopSongs.js after getting results
  const updateTracklist = (array) => {
    setTracklist(array);
  }

  // function to add tracks to playlist, will be called from Track.js
  const addToPlaylist = (newTrack) => {
    if (playlist.some((t) => t.id === newTrack.id)) {
    } else {
      setPlaylist((prev) => [...prev, newTrack]);
    }
  };

  // function to remove tracks from playlist, will be called from Track.js
  const removeFromPlaylist = (trackId) => {
    setPlaylist((prev) => prev.filter(n => n.id !== trackId));
  };

  // function to get user data
  const currentUser = async () => {
    try {
      const response = await fetch('https://api.spotify.com/v1/me', {
        method: 'GET',
        headers: {
          Authorization: `Bearer ${token}`
        }
      });

      if (!response.ok) {
        localStorage.removeItem("token");
        window.location.reload(); 
        throw new Error('Network response was not ok');
      }

      const userData = await response.json();
      return userData; 
    } catch (error) {
      throw error; 
    }
  }

  // Define the currentUser function using useCallback
  const fetchCurrentUser  = useCallback(async () => {
    // Your code to fetch user data here
  }, []); // Empty dependency array since currentUser doesn't depend on any props or state

  // update the user state
  useEffect(() => {
    const fetchUserData = async () => {
      try {
        const userData = await fetchCurrentUser();
        setUser(userData);
      } catch (error) {
        console.error('Error fetching user data:', error);
      }
    };

    if (token) {
      fetchUserData();
    }
  }, [token, fetchCurrentUser]);

  // Function to create a playlist (assuming you have the user's ID)
  const createPlaylist = async (userId, playlistName, token) => {
    // ... (unchanged code for playlist creation)
  }


  return (
    <div className="App" style={{minHeight: '100vh'}}>
      <div className="title">
        <h1>TopTunes</h1>
      </div>
      <TopSongs token={token} updateTracklist={updateTracklist} />
      {token ? (
        <div className='flex'>
          <Tracklist data={tracklist} addToPlaylist={addToPlaylist} />
          <Playlist
            className='flexItem'
            playlist={playlist}
            data={tracklist}
            removeFromPlaylist={removeFromPlaylist}
            setPlaylistName={setPlaylistName}
            playlistName={playlistName}
            currentUser={currentUser}
            createPlaylist={createPlaylist}
            token={token}
          />
        </div>
      ) : ''}
    </div>
  );
}

export default App;
