import { useEffect, useState } from "react";
import './App.css';
import axios from 'axios';
import { CLIENT_ID, REDIRECT_URI, AUTH_ENDPOINT } from "./config";

function App() {
    const RESPONSE_TYPE = "token";

    const [token, setToken] = useState("");
    const [artists, setArtists] = useState([]);

    useEffect(() => {
        const hash = window.location.hash;
        let storedToken = window.localStorage.getItem("token");

        if (!storedToken && hash) {
            storedToken = hash.substring(1).split("&").find(elem => elem.startsWith("access_token")).split("=")[1];

            window.location.hash = "";
            window.localStorage.setItem("token", storedToken);
        }

        setToken(storedToken);
    }, []);

    const logout = () => {
        setToken("");
        window.localStorage.removeItem("token");
    };

    const fetchTopArtists = async () => {
        try {
            const { data } = await axios.get("https://api.spotify.com/v1/me/top/artists", {
                headers: {
                    Authorization: `Bearer ${token}`
                },
            });

            setArtists(data.items);
        } catch (error) {
            console.error("Error fetching top artists:", error);
        }
    };

    return (
        <div className="App">
            <h1>Spotify React</h1>
            {!token ?
                <a href={`${AUTH_ENDPOINT}?client_id=${CLIENT_ID}&scope='&redirect_uri=${REDIRECT_URI}&response_type=${RESPONSE_TYPE}`}>Login to Spotify</a>
                : (
                    <>
                        <button onClick={logout}>Logout</button>
                        <button onClick={fetchTopArtists}>Fetch Top Artists</button>
                    </>
                )}

            {token && artists.length > 0 ?
                <div>
                    <h2>Top Artists</h2>
                    {artists.map(artist => (
                        <div key={artist.id}>
                            {artist.images.length ? <img width={"100%"} src={artist.images[0].url} alt="" /> : <div>No Image</div>}
                            {artist.name}
                        </div>
                    ))}
                </div>
                : <h2>Please login or fetch top artists</h2>
            }
        </div>
    );
}

export default App;
