// TopTracksButton.js
import React from "react";
import axios from 'axios';

const TopTracksButton = ({ onFetchTopTracks }) => {
  const fetchTopTracks = async () => {
    try {
      const { data } = await axios.get('https://api.spotify.com/v1/me/top/tracks?limit=10', {
        headers: {
          Authorization: `Bearer ${localStorage.getItem("token")}`
        },
      });

      onFetchTopTracks(data.items);
    } catch (error) {
      console.error("Error fetching top tracks:", error);
    }
  };

  return <button className="my-button" onClick={fetchTopTracks}>Show my Top 10 tracks</button>;
};

export default TopTracksButton;
