import React, { useEffect, useState } from "react";
import axios from 'axios';

const TopTracks = ({ token }) => {
    const [tracks, setTracks] = useState([]);
    const [artists, setArtists] = useState([]);
    const [showArtists, setShowArtists] = useState(false);

    const fetchTopTracks = async () => {
        try {
            const { data } = await axios.get('https://api.spotify.com/v1/me/top/tracks?limit=10', {
                headers: {
                    Authorization: `Bearer ${token}`
                },
            });

            setTracks(data.items);
        } catch (error) {
            console.error("Error fetching top tracks:", error);
        }
    };

    const fetchTopArtists = async () => {
        try {
            const { data } = await axios.get('https://api.spotify.com/v1/me/top/artists?limit=10', {
                headers: {
                    Authorization: `Bearer ${token}`
                },
            });

            setArtists(data.items);
        } catch (error) {
            console.error("Error fetching top artists:", error);
        }
    };

    useEffect(() => {
        if (token) {
            fetchTopTracks();
        }
    }, [token]);

    const toggleShowArtists = () => {
        if (!showArtists) {
            fetchTopArtists();
        }
        setShowArtists(!showArtists);
    };

    return (
        <div>
            <h2>Top tracks</h2>

            <button onClick={toggleShowArtists}>
                {showArtists ? "Hide Artists" : "Show Artists"}
            </button>

            {showArtists && (
                <div>
                    <h2>Top artists</h2>
                    {artists.map(artist => (
                        <div key={artist.id}>
                            {artist.name}
                        </div>
                    ))}
                </div>
            )}
        </div>
    );
}

export default TopTracks;
